#urllib

import urllib.request  as request
import urllib.error as error

# html 문서와 그림 파일 다운받기
# 그림파일 저작권x

# 그림파일
imageUrl = "data:image/jpeg;base64,"

# html 문서(naver main)
htmlUrl = "http://www.naver.com/"

# 이미지 다운받기
# 예외처리는 예외가 발생할 것 같은 코드에 넣어준다.
try:
    ret = request.urlretrieve(imageUrl, './download/download_image.png')
    print(ret)
except error.HTTPError as e:
    print("ERROR: ", e.code)
except Exception as e:
    print(e)

#html 문서 다운받기(.csv)
ret_1 = request.urlretrieve(htmlUrl, './download/write.csv')
print(ret_1)

#html 문서 다운받기(.html)
try:
    ret_2 = request.urlretrieve(htmlUrl, './download/download_document.html')
    print(ret_2)
except error.HTTPError as e:
    print("ERROR: ", e.code)
except Exception as e:
    print(e)